using Test

function rna_transcription(sequence::String)
	replace(sequence, "T" => "U")
end

@testset "Sample & Test Datasets" begin
	sample_dataset = "GATGGAACTTGACTACGTAAATT"
	file = open("datasets/rosalind_rna.txt", "r")
	test_dataset = read(file, String)
	close(file)

	@test sample_dataset |> rna_transcription == "GAUGGAACUUGACUACGUAAAUU"
	@test test_dataset   |> rna_transcription == "\n"
end; nothing
